const NUMBER_OF_LEAVES = 35;


function init()
{
    var container = document.getElementById('leafContainer');
    for (var i = 0; i < NUMBER_OF_LEAVES; i++)
    {
        container.appendChild(createALeaf());
    }
}


function randomInteger(low, high)
{
    return low + Math.floor(Math.random() * (high - low));
}


function randomFloat(low, high)
{
    return low + Math.random() * (high - low);
}


function pixelValue(value)
{
    return value + 'px';
}

function durationValue(value)
{
    return value + 's';
}

function createALeaf()
{

    var leafDiv = document.createElement('div');
    var image = document.createElement('img');


    image.src = 'images/money.svg';

    leafDiv.style.top = "-50px";

    leafDiv.style.left = pixelValue(randomInteger(0, document.querySelector(".game").clientWidth));


    var spinAnimationName = (Math.random() < 0.5) ? 'clockwiseSpin' : 'counterclockwiseSpinAndFlip';

    leafDiv.style.webkitAnimationName = 'fade, drop2';
    image.style.webkitAnimationName = spinAnimationName;


    var fadeAndDropDuration = durationValue(randomFloat(5, 11));


    var spinDuration = durationValue(randomFloat(4, 8));

    leafDiv.style.webkitAnimationDuration = fadeAndDropDuration + ', ' + fadeAndDropDuration;

    var leafDelay = durationValue(randomFloat(0, 5));
    leafDiv.style.webkitAnimationDelay = leafDelay + ', ' + leafDelay;

    image.style.webkitAnimationDuration = spinDuration;

    leafDiv.appendChild(image);

    return leafDiv;
}


